
public class NameOfTheMonths {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Create a Java program and declare int variable that will hold a month.
		//Based on the value of the variable your program should print the name of the month.
		
		int month=9;
		if(month==1) {
			System.out.println("The month is january");
		}else if (month==2) {
			System.out.println("The month is Februay");
		}else if (month==3) {
			System.out.println("The month is march");
		}else if (month==4) {
			System.out.println("The month is April");
		}else if (month==5) {
			System.out.println("The month is May");
		}else if (month==6) {
			System.out.println("The month is June");
		}else if (month==7) {
			System.out.println("The month is July");
		}else if(month==8) {
			System.out.println("The month is August");
		}else if(month==9) {
			System.out.println("The month is September");
		}else if (month==10) {
			System.out.println("The month is October");
		}else if (month==11) {
			System.out.println("The month is November");
		}else if (month==12) {
			System.out.println("The month is December");
		}
	}

}
