
public class RelationalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=10;
		int num1=20;
		System.out.println(num>num1);  // num is bigger than num1 
		System.out.println(num!=num1);   // num is not equal to num1
		System.out.println(num<num1);     //
		System.out.println(num==num1);    // is num is equal to num1
		
		int a=100;
		int b=190;
		boolean boo=a<b;
		System.out.println(boo);
		
		System.out.println(a==b);
		System.out.println(a=b);  // we reassigned (a) to (b)
		
		System.out.println(a<b);  // because we reassigned the value of a thats why it shows false 
		System.out.println(a<=b);
		
		
		
		
	}

}
