package com.ForLoop;

public class AnotherLoopTask {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//task1
		
		for(int i=1; i<=100; i++) {
			System.out.print(i+" ");
		}
		//task2
		
		
		for(int num=100; num>=1; num--) {
			System.out.print(num+" ");
		}
		
		//task3
		for( int t=20; t>=1; t+=2) {
			System.out.println();
		}
		for(int b=20; b>=1; b++) {
			if(b%2==0) {
				System.out.println(b);
				
			}
			
		}
	}

}
