package com.ForLoop;

public class TaskLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int num=1;
		while (num<101 ) {
			System.out.print(num+" ");
			num++;
		}
		
		int num1=100;
		while(num1>=1) {
			System.out.println(num1);
			num1--;
		}
		
		int n=100;
		while(n>=20 ) {
			if(n%2==0) {
			System.out.println(n);
		
		}
			n--;
	}
		int n1=1;
		while (n<=100) {
			if(n1%2!=0) {
				System.out.println(n1);
				n1--;

	}
	
}

}
}
