package com.Tasks;

public class LogicalOperators2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		/*
		 * if a number is from 0 to 100 print number is in rage
		 * else print number is out range
		 * 
		 */
	
	int number=1000;
	
	if (number>=0) {
		if(number<=100) {
			System.out.println("number is in rage");
		}else {
			System.out.println("number is out of range");
		}
		
	}else {
		System.out.println("number is out of range");
	}
		
	}

}
