package com.secondClass;

public class Identifiers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//RULES:
		/* 
		 * Identifiers - name we give to classes. variables and methods 
		 * keywords can not be used as identifiers 
		 * identifiers can not start with numbers
		 * identifiers cannot have special characters except: _ AND $
		 */
		
		
		char singleletter='c';
		//boolean new=true; do not use keyword to name your variables 
		
		//int num1=100;  // error 
		
		boolean boo_=true; 
		double $price=2.99;
		
		
		// preferences:
		/* follow camel casing
		 * java classes should start with upper case and follow camel casing 
		 * variables and methods in java should start with lowercase and follow camel casing
		 * 
	
		 */
		short thisIsMyShortNumber=10;
		System.out.println(thisIsMyShortNumber); 
		

	}

}
